mapper.select:
select custid, name
from customer
where custid=#{custid} and password=#{password}

select custid
from customer
where custid=#{custid}

select custid, name, email, division, idno, address
from customer
where custid=#{custid}

mapper.update:
update customer
set password=#{password}, name=#{name}, email=#{email},
	division=#{division}, idno=#{idno}, address=#{address}
where custid=#{custid} and password=#{password}
